    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %} Connexion Admin {% endblock %}

{% block main %}
<style>
    .example-wrapper { margin: 1em auto; max-width: 800px; width: 95%; font: 18px/1.5 sans-serif; }
    .example-wrapper code { background: #F5F5F5; padding: 2px 6px; }
</style>

    {% if error %}
        <div>{{ error.messageKey|trans(error.messageData, 'security') }}</div>
    {% endif %}

<div class=\"example-wrapper\">
    <h1 class=\"text\">Bonjour! ✅</h1>
    <img class=\"logoIcone\" src=\"{{ asset('assets/img/logo_petit_gascon.png') }}\" alt=\"Logo Restaurant\">

    <div class=\"blocForm\">
        <h4 class=\"text\">Connexion à l'espace d'administration</h4>
        <form action=\"{{ path('app_login') }}\" method=\"post\" class=\"champsForm\">
            <label for=\"username\">Email:</label>
            <input type=\"text\" id=\"username\" name=\"_username\" value=\"{{ last_username }}\" class=\"example-wrapper\">

            <label for=\"password\"> Votre mot de passe: </label>
            <input type=\"password\" id=\"password\" name=\"_password\" class=\"example-wrapper\">

            <button type=\"submit\" class=\"connect\"> Connexion </button>
        </form>
    </div>
</div>
{% endblock %}
", "login/index.html.twig", "/Applications/MAMP/htdocs/petitGascon/templates/login/index.html.twig");
