    public function getSourceContext()
    {
        return new Source("{% extends 'admin/home.html.twig' %}

{% block title %}Categorie{% endblock %}

{% block main %}
    <div class=\"content\">

        <h1>Liste des Categories</h1>

        <table class=\"table\">
            <thead>
                <tr class=\"tableau\">
                    <th>Id</th>
                    <th>Titre</th>
                    <th>actions</th>
                </tr>
            </thead>
            <tbody>
            {% for category in categories %}
                <tr class=\"tableau\">
                    <td>{{ category.id }}</td>
                    <td>{{ category.titre }}</td>
                    <td class=\"bouton-tableau\">
                        <a class=\"btn connect\" href=\"{{ path('admin_app_category_show', {'id': category.id}) }}\">Voir</a>
                        <a class=\"btn connect\" href=\"{{ path('admin_app_category_edit', {'id': category.id}) }}\">Editer</a>
                    </td>
                </tr>
            {% else %}
                <tr>
                    <td colspan=\"3\">no records found</td>
                </tr>
            {% endfor %}
            </tbody>
        </table>

        <a class=\"btn connect\" href=\"{{ path('admin_app_category_new') }}\">Créer une nouvelle catégorie</a>
    </div>
{% endblock %}
", "admin/category/index.html.twig", "/Applications/MAMP/htdocs/petitGascon/templates/admin/category/index.html.twig");
